AutoScalingGroupName.queryValue:
SELECT DISTINCT(JSONExtractString(labels, 'AutoScalingGroupName'))
FROM signoz_metrics.time_series_v2
WHERE JSONExtractString(labels, 'ClusterName') = {{ .ClusterName }}

InstanceId.queryValue:
SELECT DISTINCT(JSONExtractString(labels, 'InstanceId'))
FROM signoz_metrics.time_series_v2
WHERE JSONExtractString(labels, 'AutoScalingGroupName') = {{ .AutoScalingGroupName }}
